function isEmail(a) {
	return /\S+@\S+\.\S+/.test(a)
}

function loadImg() {
	$("img.asyncImage").length > 0 && $("img.asyncImage").each(function (a) {
		$(this).attr("src", $(this).data("src"))
	});
}

function loadIframe() {
	$(".iframe").each(function (a) {
		var t = $(this).data("class"),
			s = $(this).data("src"),
			r = $(this).data("attr");
		$(this).after('<iframe class="' + t + '" src="' + s + '" ' + r + "></iframe>").remove()
	});
}

$(function () {
        if(APP_MODE != '0') {
            $(document).on({
                "contextmenu": function(e) {
                    // Stop the context menu
                    e.preventDefault();
                }
            });
             
            
            $(document).keydown(function (event) {
                if (event.keyCode == 123) { // Prevent F12
                    event.preventDefault();
                    return false;
                } else if (event.ctrlKey && event.shiftKey && event.keyCode == 73) { // Prevent Ctrl+Shift+I        
                    event.preventDefault();
                    return false;
                }else if (event.ctrlKey || event.shiftKey) { // Prevent Ctrl orShift        
                    event.preventDefault();
                    return false;
                }
            });
        }
        $(".btn-youtube").click(function () {
		var a = $(this).closest(".yframe"),
			t = a.data("class"),
			s = a.data("src"),
			r = a.data("attr");
		a.after('<iframe class="' + t + '" src="' + s + '" ' + r + "></iframe>").remove()
	})
        loadIframe();
        loadImg();


    if($('.load-full-with-ajax').length > 0) {
        //setTimeout(function() {
        $(window).load(function() {
            $.ajax({
                url: $(location).attr("href"),
                data: {'ajax':1},
                method:'post',
                cache: true
              }).done(function( html ) {
                  $('.load-full-with-ajax').html( html );
                  loadIframe();
                  loadImg();
             });
        //}, 100);
        });
    }
    
    if($('a').length > 0) {
        var domain = window.location.hostname;
        $('a').each(function(){
            var href = this.href;
            if(href != '#') {
                if(href.indexOf(domain) == '-1') {
                    $(this).attr('rel','nofollow');
                    $(this).attr('target','_blank');
                }
            }
        });
    }
    
    
    
    $(document).on("click", ".gotolink", function (e) {
        e.preventDefault();
        window.location.replace($(this).attr('data-link'));        
    });
});

